mapper.select:
SELECT * FROM activities


        AND type_id = #{typeId}


        AND status = #{status}


ORDER BY start_time DESC

mapper.insert:
INSERT INTO activities (
    type_id, title, description, start_time, end_time, location,
    image, organizer, participants, max_participants, is_online,
    status, create_time, update_time
) VALUES (
    #{typeId}, #{title}, #{description}, #{startTime}, #{endTime}, #{location},
    #{image}, #{organizer}, #{participants}, #{maxParticipants}, #{isOnline},
    #{status}, #{createTime}, #{updateTime}
)
